"use strict";function anchorLinkHandler(e){e.preventDefault();var n=this.href.slice(this.href.indexOf("#")),t=document.querySelector(n),r=t.getBoundingClientRect().top,o=t.getBoundingClientRect().left;window.scrollBy({top:r,left:o,behavior:"smooth"})}var linksToAnchors=document.querySelectorAll('a[href^="#"]');linksToAnchors.forEach(function(e){return e.addEventListener("click",anchorLinkHandler)});